mapper.update:
UPDATE o_teacher
SET name = #{teacher.name}, department_id = #{teacher.department}, gender = #{teacher.gender}
, email = #{teacher.email}, phone = #{teacher.phone}
WHERE id = #{teacher.id}

UPDATE t_user
SET username = #{user.username}, password = #{user.password}, roleId = #{user.roleId}
, available = #{user.available}, name = #{user.name}, staff = #{user.staff},phone = #{user.phone}
WHERE phone = #{user.phone}
